mapper.delete:
DELETE FROM t_template
WHERE id IN

	#{id}

mapper.update:
UPDATE t_template
name = #{name}, type = #{type}, biz_type = #{bizType}, content = #{content}, status = #{status}, updater = #{updater}, update_time = NOW()
WHERE id = #{id}

mapper.select:
SELECT id, name, type, biz_type, content, status, creator, create_time, updater, update_time FROM t_template
AND name LIKE concat('%',#{name},'%')
AND type = #{type}
AND biz_type LIKE concat('%',#{bizType},'%')
AND status = #{status}
ORDER BY id DESC
